        return function (expr) {
            return expr;
        };
    };

    context[that.names.statement] = function (i) {
        var node = that.nodes[i];
        that.emit('node', node);
    };

    context[that.names.block] = function (i) {
        that.emit('block', i);
    };

    return context;
