                            "callee": {
                                "type": "Identifier",
                                "name": that.names.expression
                            },
                            "arguments": [
                                {
                                    "type": "Identifier",
                                    "name": that.nodeCounter++
                                }
                            ]
                        },
                        node
                    ]
                }

                that.nodes[that.nodeCounter - 1] = node;
                node.id = that.nodeCounter - 1;

                return newNode
            }
